typedef struct ImagePattern {
  int frames;
  uint8_t* pixels;
} ImagePattern;

ImagePattern new_image_pattern(uint8_t arr[]) {
  ImagePattern* imagepattern = (ImagePattern)malloc(sizeof( ImagePattern));
  imagepattern->frames = sizeof(arr);
  imagepattern->pixels = arr;
  return imagepattern;
}

